                }                    }                    else if($(this).children('a').html() == 'Events')                    {                        url += 'events';                    }                    else if($(this).children('a').html() == 'Stands')                    {                        url += 'stands';                    }                    else if($(this).children('a').html() == 'Menu Items')                    {                        url += 'menu_items';                    }                    else if($(this).children('a').html() == 'Promos')                    {                        url += 'promos';                    }                    else if (($(this).children('a').html() == 'Large Promos'))                    {                        url += 'largepromos';                    }                    else if (($(this).children('a').html() == 'Display Control'))                    {                        url += 'stands_control';                    }                    $('.stand_nav').removeClass('selected');                    $(this).addClass('selected');                    $('.hero-unit-style').fadeOut(250, function(){                        $.ajax({                            url:url,                            async:false,                            data:postdata,                            type:"POST",                            success:function(data){                                $('.hero-unit').html(data);                                $('.hero-unit-style').fadeIn(250);                            }                        });                    });                });            });        </script>        <script type="text/javascript">            $(document).ready(function(){                $('.logout-btn').click(function(){                    $.ajax({                        url:"/c/patriots/home/logout",                        async:false,                        success:function(data)                        {                            if(data)                            {                                window.location = '../icg/login';                            }                        }                    });                });                $('.push-to-all-btn').click(function(){                    $.ajax({    					url:"/c/patriots/ajax/waffle/push_all",    					async:false,                        dataType:"json",    					success:function(data){                            if(data.meta.status == 1)                            {                                alert('Saved to all stands successfully!');                            }                            else                            {                                alert('There was an error saving to all stands. Please contact ICG to resolve this issue.');                            }    					}    				});                });            });        </script>    </head>    <body>        <div class="container">            <div class="style-container">                <div class="navbar navbar-fixed-top">                    <div class="navbar-inner">                        <div class="container-fluid">                            <div class="logo"></div><a href="#" class="brand">NEW ENGLAND PATRIOTS<a href="#" class="subbrand"> Digital Display Control Panel</a>                        </div>                        <div class="btn push-to-all-btn btn-success">Save Changes</div>                        <div class="btn logout-btn btn-danger">Logout</div>                    </div>                </div>                <div class="container-fluid">                    <div class="row-fluid">                        <div class="span3">                            <div class="well sidebar-nav">                                <ul class="nav nav-list">                                    <li class="nav-header nav-title">Power Management</li>                 